import React from 'react'
import "./Lawyers.css"
function Lawyers() {
  const [showProfiles, setShowProfiles] = useState(false);

  const handleCategoryClick = () => {
    setShowProfiles(!showProfiles);
  };
  return (
    <div className='lawyers'>
      <div className='grid'>
      <div className='category' onClick={handleCategoryClick}>
          <span>Criminal Law</span>
        </div>
        {showProfiles && (
          <div className='profiles'>
            {/* Add your profiles content here */}
            <div className='profile'>Profile 1</div>
            <div className='profile'>Profile 2</div>
            {/* You can render dynamic content for the profiles */}
          </div>
        )}
        <div className='category'><span>Civil Law</span></div>
        <div className='category'><span>Constitutional Law</span></div>
        <div className='category'><span>Administrative Law</span></div>
        <div className='category'><span>Family Law</span></div>
        <div className='category'><span>Environmental Law</span></div>
        <div className='category'><span>International Law</span></div>
        <div className='category'><span>Contract Law</span></div>
        <div className='category'><span>Tort Law</span></div>
        <div className='category'><span>Property Law</span></div>
        <div className='category'><span>Employment Law</span></div>
        <div className='category'><span>Intellectual Property Law</span></div>
        <div className='category'><span>Banking and Finance Law</span></div>
        <div className='category'><span>Healthcare Law</span></div>
        <div className='category'><span>Healthcare Law</span></div>
        <div className='category'><span>Criminal Procedure Law</span></div>
        <div className='category'><span>Entertainment Law</span></div>
        <div className='category'><span>Military Law</span></div>
      </div>
    </div>
  )
}

export default Lawyers
