import React, { useState } from 'react';
import "./Ask.css";

function Ask() {
  const [selectedOption, setSelectedOption] = useState(null);
  const [paymentSuccess, setPaymentSuccess] = useState(false);

  const handleOptionSelect = (option) => {
    setSelectedOption(option);
  };

  const handlePayment = () => {
    setPaymentSuccess(true);
  };

  const resetPaymentState = () => {
    setSelectedOption(null);
    setPaymentSuccess(false);
  };

  return (
    <>
      <div className='ask'>
        <h1>Having any legal troubles?</h1>
      </div>
      <div className="con">
        <div className="cardw" onClick={() => handleOptionSelect('chat')}>
          <a href="http://"><i className="fa-solid fa-comments"></i></a>
          <p>Chat with Expert</p>
        </div>
        <div className="cardw" onClick={() => handleOptionSelect('call')}>
          <a href="http://"><i className="fa-solid fa-phone"></i></a>
          <p>Call with Expert</p>
        </div>
        {selectedOption && (
          <button onClick={handlePayment}>Pay Now</button>
        )}
      </div>
      {paymentSuccess && (
        <div className="dialog">
          <p>Money transaction successful!</p>
          <button onClick={resetPaymentState}>Close</button>
        </div>
      )}
    </>
  );
}

export default Ask;
