import React, { useState } from 'react';
import './Profile.css'; 
import { useNavigate } from 'react-router-dom';
import ReviewPage from "../review/review";

const Profile = () => {
  const lawyerData = {
    name: 'John Doe',
    aboutMe: 'I am an experienced lawyer with expertise in various areas of law...',
    cases: ['Case 1', 'Case 2', 'Case 3'],
    reviews: [
      {
        name: 'Client 1',
        review: 'Excellent lawyer! Highly recommended.',
        stars: 5,
      },
      {
        name: 'Client 2',
        review: 'John helped me win my case. Very knowledgeable.',
        stars: 4,
      },
    ],
  };

  const navigate = useNavigate();
  const [reviewText, setReviewText] = useState('');
  const [reviews, setReviews] = useState(lawyerData.reviews); // Initialize with existing reviews

  const handleBookAppointmentClick = async () => {
    try {
      navigate("/appointment");
    } catch (err) {
      console.log(err);
    }
  }

  const handleReview = async () => {
    try {
      navigate("/review");
    } catch (err) {
      console.log(err);
    }
  }

  const handleChatClick = async () => {
    try {
      navigate("/messages");
    } catch (err) {
      console.log(err);
    }
  }

  const handleReviewSubmit = () => {
    if (reviewText.trim() === '') {
      alert('Please enter a review.');
      return;
    }

    // Create a new review object
    const newReview = {
      name: 'User', // You can customize this
      review: reviewText,
      stars: 5, // You can add a star rating input if needed
    };

    // Add the new review to the list of reviews
    setReviews([...reviews, newReview]);

    // Clear the review text input
    setReviewText('');
  }

  return (
    <div className="profile-container">
      <div className="profile-header">
        <img src="https://picsum.photos/200/300" alt={lawyerData.name} className="profile-picture" />
        <h1>{lawyerData.name}</h1>
      </div>

      <div className="about-me">
        <h2>About Me</h2>
        <p>{lawyerData.aboutMe}</p>
      </div>

      <div className="cases">
        <h2>Cases</h2>
        <ul>
          {lawyerData.cases.map((caseItem, index) => (
            <li key={index}>{caseItem}</li>
          ))}
        </ul>
      </div>

      <div className="reviews">
        <h2>Reviews</h2>
        <ul>
          {reviews.map((review, index) => (
            <li key={index}>
              <div className="review-header">
                {/* Render review data */}
              </div>
              <p className="review-text">{review.review}</p>
            </li>
          ))}
        </ul>
      </div>

      {/* Review input and submit button */}
      <div className="review-input">
        <textarea
          placeholder="Write your review here..."
          value={reviewText}
          onChange={(e) => setReviewText(e.target.value)}
        />
        <button onClick={handleReviewSubmit}>Submit Review</button>
      </div>

      <button onClick={handleBookAppointmentClick}>Book An Appointment</button>
      <button onClick={handleReview}>Give Review</button>
      <button onClick={handleChatClick}>Chat with Lawyer</button>
    </div>
  );
};

export default Profile;
