import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useReviewContext } from '../../Components/ReviewContext/ReviewContext';
import Rating from 'react-rating-stars-component';

const ReviewPage = () => {
  const navigate = useNavigate();
  const { addReview } = useReviewContext();
  const [reviewText, setReviewText] = useState('');
  const [starRating, setStarRating] = useState(0);

  const handlePostReview = () => {
    if (reviewText && starRating > 0) {
      addReview({ text: reviewText, rating: starRating });
      navigate('/profile'); // Redirect to the profile page
    } else {
      alert('Please provide a review text and rating.');
    }
  };

  return (
    <div className="review-container">
      <h1>Write a Review</h1>
      <textarea
        placeholder="Enter your review here..."
        value={reviewText}
        onChange={(e) => setReviewText(e.target.value)}
      />
      <div className="star-rating">
        <Rating
          count={5}
          value={starRating}
          onChange={(newRating) => setStarRating(newRating)}
          size={30}
          color="#ffd700"
        />
      </div>
      <button onClick={handlePostReview}>Post Review</button>
    </div>
  );
};

export default ReviewPage;
